import React from 'react'
import Card from './Card';

const Cards = (props) => {

    let data = props.courses;

    
    function getCourses(){
        Object.values(data).forEach( array => {
            array.forEach( (courseData) => {
                allCourses.push(courseData);
            })
        })
        return allCourses;
    }

  return (
    <div>
        {
            getCourses().map( (course) => {
                return (
                    <Card key={course.id}/>
                );
            })
        }
    </div>
  )
}

export default Cards;
